import { useState } from "react";
import { userdata } from "./user";


export function Showdataintable()
{
    const [query,setquery] = useState();
   
    return(
        <>
        <button>Search</button>
        <input type="text" value={query} onChange={(e)=>{setquery(e.target.value)}}/>
        <table><tr><th>Name</th><th>User Name</th><th>E-mail</th></tr><tr><td>{userdata.filter((i)=>i.name.includes(query)).
       map((i)=>(<td>{i.name}</td>))}</td></tr></table>
     
        </>
    )
}
